import { build } from "esbuild";
import { writeFileSync } from "fs";
import { fileURLToPath } from "url";
import { dirname, join } from "path";

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

try {
  const result = await build({
    entryPoints: [
      join(__dirname, "../server/routes/mcp/openai-compat-runtime.ts"),
    ],
    bundle: true,
    write: false,
    format: "iife",
    platform: "browser",
    target: "es2020",
    minify: false,
  });

  const bundledCode = result.outputFiles[0].text;
  const serializedCode = JSON.stringify(bundledCode);

  const outputContent = `// This file is auto-generated by scripts/bundle-openai-compat-runtime.js
// Do not edit directly - modify server/routes/mcp/openai-compat-runtime.ts instead

export const OPENAI_COMPAT_RUNTIME_SCRIPT = ${serializedCode};
`;

  const outputTsPath = join(
    __dirname,
    "../server/routes/mcp/openai-compat-runtime.bundled.ts",
  );
  writeFileSync(outputTsPath, outputContent);
  console.log("✅ Successfully bundled OpenAI compat runtime script");
} catch (error) {
  console.error("❌ Failed to bundle OpenAI compat runtime script:", error);
  process.exit(1);
}
